mapper.update:

UPDATE tbl_bbs
   SET group_order = group_order + 1
 WHERE group_id = #{groupId}        
   AND group_order > #{groupOrder}

UPDATE tbl_bbs
   SET state = 1
 WHERE bbs_id = #{bbsId}

mapper.select:
SELECT bbs_id, b.user_id, contents, state, depth, group_id, group_order, modify_dt, created_dt, u.user_name
  FROM tbl_bbs b INNER JOIN tbl_user u
    ON b.user_id = u.user_id

  contents LIKE CONCAT('%', #{contents}, '%')
  AND user_name LIKE CONCAT('%', #{userName}, '%')
  AND created_dt >= #{beginDt}
  AND created_dt <= #{endDt}

 ORDER BY group_id DESC, group_order ASC
 LIMIT #{offset}, #{display}
